import React from "react";
import adapter from "webrtc-adapter";

function trace(arg) {
  var now = (window.performance.now() / 1000).toFixed(3);
  console.log(now + ": ", arg);
}

export class PeerConnection extends React.Component {
  state = {
    startDisabled: false,
    callDisabled: true,
    hangUpDisabled: true,
  };

  constructor(props) {
    super(props);

    this.localVideoref = React.createRef();
    this.remoteVideoref = React.createRef();
    this.localStream = null;
  }
  start = () => {
    this.setState({
      startDisabled: true,
    });
    navigator.mediaDevices
      .getUserMedia({
        audio: true,
        video: true,
      })
      .then(this.gotStream)
      .catch((e) => alert("getUserMedia() error:" + e.name));
  };
  gotStream = (stream) => {
    this.localVideoref.current.srcObject = stream;
    this.localStream = stream;
    this.setState({
      callDisabled: false,
    });
  };

  call = () => null;
  hangUp = () => null;

  render() {
    const { startDisabled, callDisabled, hangUpDisabled } = this.state;

    return (
      <div>
        <video ref={this.localVideoRef}></video>
        <video ref={this.remoteVideoRef}></video>

        <div>
          <button onClick={this.start} disabled={startDisabled}>
            Start
          </button>
          <button onClick={this.call} disabled={callDisabled}>
            Call
          </button>
          <button onClick={this.hangUp} disabled={hangUpDisabled}>
            Hang up
          </button>
        </div>
      </div>
    );
  }
}
