import React from "react";
import adapter from "webrtc-adapter";

function trace(arg) {
  var now = (window.performance.now() / 1000).toFixed(3);
  console.log(now + ": ", arg);
}

const constraints = { video: true };

const success = (stream) => {
  window.localStream = stream;
  this.localVideoRef.current.srcObject = stream;
  this.pc.addStream(stream);
};

const fail = (e) => {
  console.log("getUserMedia error: ", e);
};

navigator.mediaDevices.getUserMedia(constraints).then(success).catch(fail);

export class PeerConnection extends React.Component {
  state = {
    startDisabled: false,
    callDisabled: true,
    hangUpDisabled: true,
  };

  localVideoref = React.createRef();
  remoteVideoref = React.createRef();
  //localStream = null;

  start = () => {
    this.setState({
      startDisabled: true,
    });
    navigator.mediaDevices
      .getUserMedia({
        audio: true,
        video: true,
      })
      .then(this.success)
      .catch((e) => alert("getUserMedia() error:" + e.name));
  };

  success = (stream) => {
    window.localStream = stream;
    this.localVideoRef.current.srcObject = stream;
    this.pc.addStream(stream);
  };

  /*gotStream = (stream) => {
    this.localVideoRef.current = stream;
    this.setState({
      callDisabled: false,
      localStream: stream,
    });
  }; */

  call = () => null;
  hangUp = () => null;

  render() {
    const { startDisabled, callDisabled, hangUpDisabled } = this.state;

    return (
      <div>
        <video ref={this.localVideoRef} autoPlay></video>
        <video ref={this.remoteVideoRef}></video>

        <div>
          <button onClick={this.start} disabled={startDisabled}>
            Start
          </button>
          <button onClick={this.call} disabled={callDisabled}>
            Call
          </button>
          <button onClick={this.hangUp} disabled={hangUpDisabled}>
            Hang up
          </button>
        </div>
      </div>
    );
  }
}
