import React, { Component } from "react";
import io from "socket.io-client";
//import logo from "./logo.svg";
import "./App.css";
//import { PeerConnection } from "./PeerConnection";

class App extends Component {
  constructor(props) {
    super(props);

    this.localVideoRef = React.createRef();
    this.remoteVideoRef = React.createRef();

    this.socket = null;
    this.candidates = [];
  }

  componentDidMount = () => {
    this.socket = io("/webrtcPeer", {
      path: "/web-rtc-audio",
      query: {},
    });

    this.socket.on("connection-success", (success) => {
      console.log(success);
    });

    this.socket.on("offerOrAnswer", (sdp) => {
      this.textref = JSON.stringify(sdp);
    });

    this.socket.on("candidate", (candidate) => {
      this.candidates = [...this.candidates, candidate];
    });

    const pc_config = null;

    this.pc = new RTCPeerConnection(pc_config);

    this.pc.onicecandidate = (e) => {
      if (e.candidate) {
        this.sendToPeer("candidate", e.candidate);
      }
    };

    this.pc.oniceconnectionstatechange = (e) => {
      console.log(e);
    };

    this.pc.ontrack = (e) => {
      this.remoteVideoRef.current.srcObject = e.streams[0];
    };

    const constraints = { video: true };

    const success = (stream) => {
      window.localStream = stream;
      this.localVideoRef.current.srcObject = stream;
      this.pc.addStream(stream);
    };

    const fail = (e) => {
      console.log("getUserMedia error: ", e);
    };

    navigator.mediaDevices.getUserMedia(constraints).then(success).catch(fail);
  };

  sendToPeer = (messageType, payload) => {
    this.socket.emit(messageType, {
      socketID: this.socket.id,
      payload,
    });
  };

  createOffer = () => {
    console.log("Offer");
    this.pc.createOffer({ offerToReceiveVideo: 1 }).then((sdp) => {
      this.pc.setLocalDescription(sdp);
      this.sendToPeer("offerOrAnswer", sdp);
    });
  };

  setRemoteDescription = () => {
    const desc = JSON.parse(this.textref);

    this.pc.setRemoteDescription(new RTCSessionDescription(desc));
  };

  createAnswer = () => {
    console.log("Answer");
    this.pc.createAnswer({ offerToReceiveVideo: 1 }).then((sdp) => {
      this.pc.setLocalDescription(sdp);
      this.sendToPeer("offerOrAnswer", sdp);
    });
  };

  addCandidate = () => {
    /* const candidate = JSON.parse(this.textref.value);
    console.log("Adding candidate:", candidate);

    this.pc.addIceCandidate(new RTCIceCandidate(candidate)); */
    this.candidates.forEach((candidate) => {
      console.log(JSON.stringify(candidate));
      this.pc.addIceCandidate(new RTCIceCandidate(candidate));
    });
  };

  render() {
    return (
      <div>
        <video
          style={{
            width: 240,
            height: 240,
            margin: 5,
            backgroundColor: "black",
          }}
          ref={this.localVideoRef}
          autoPlay
        ></video>
        <video
          style={{
            width: 240,
            height: 240,
            margin: 5,
            backgroundColor: "black",
          }}
          ref={this.remoteVideoRef}
          autoPlay
        ></video>

        <button onClick={this.createOffer}>Offer</button>
        <button onClick={this.createAnswer}>Answer</button>
        <br />
        <textarea
          ref={(ref) => {
            this.textarea = ref;
          }}
        />
        <br />
        <button onClick={this.setRemoteDescription}>
          Set remote description
        </button>
        <button onClick={this.addCandidate}>Add cadidate</button>
      </div>
    );
  }
  /* render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title"> Welcome to React </h1>
        </header>
        <PeerConnection />
      </div>
    );
  } */
}

export default App;
